import styled from "styled-components";

export const ContainerLeft = styled.div`
  display: flex;
  flex-direction: column;
  position: relative;
  padding-top: 4rem;
  transition: all 300ms ease;
  background-color: var(--orange);
  .logo{
    background-color: var(--yellow);
    h2{
    font-size:2.5rem;
    }
  }
  
  .Menu{
    margin-top: 4rem;
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: var(--pink);
    .MenuItem{
      display: flex;
      align-items: center;
      gap: 1rem;
      height: 2.5rem;
      margin-left: 2rem;
      position: relative;
      transition: all 300ms ease;
      &:hover{
        cursor: pointer;
      }
    }
  }
`
