import React, { useEffect, useState } from 'react'
import { QRCode } from 'qrcode'

const Mainbar = ({ text }) => {

  const [src, setSrc] = useState('');

  useEffect(()=> {
    QRCode.toDataURL(text).then((setSrc));
  }, []);
  return (
    <>
      <div>Main</div>
      <img src={src} />
    </>
  )
}

export default Mainbar
